import React from 'react';
import { NavLink } from 'react-router-dom';
import './Home.css';
import login from '../images/login.gif'

const Home = () => {
  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-md-6 offset-md-3 text-center">
          <h1>Welcome to Grievance Management System</h1>
          <p className="mt-3">Solution for your Grievances</p>
          <div className="mt-4">
            <NavLink className="btn btn-primary mr-3" to="/login">Login</NavLink>
            <NavLink className="btn btn-success" to="/signup">Sign Up</NavLink>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
