import React,{useState} from 'react';
import { useHistory } from 'react-router-dom';

const AdminLogin =()=>{
    const [email,setEmail]=useState('');
    const [password,setPassword]=useState('');

    const history=useHistory();
    
    const getAccess=()=>{
        if(email==="Shashank@gmail.com" && password==="admin"){
            window.alert("Login Successful");
            history.push("/aAbBcC");
        }
        else if(email==="admin.cse@gmail.com" && password==="admin"){
           window.alert("Login Successful");
            history.push("/education"); 
        }
        else if(email==="admin.aids@gmail.com" && password==="admin"){
            window.alert("Login Successful");
             history.push("/health"); 
         }
         else if(email==="admin.csit@gmail.com" && password==="admin"){
            window.alert("Login Successful");
             history.push("/service"); 
         }
        else{
            window.alert("You don't have this access");
        }
    }
    return(
        <>
        <div className="login-container">
          <h1 className="text-center mb-4">Admin Login</h1>
          <hr className="divider" />
          <form method="GET">
            <div className="form-group mx-5">
              <div className="input-group mb-3">
                <span className="input-group-text" id="email-addon">
                  <i className="fas fa-envelope"></i>
                </span>
                <input type="email" name="email" id="email" autoComplete="off" 
                       value={email}
                       onChange={(e)=> setEmail(e.target.value)}
                       placeholder="Enter your email address" className='form-control'/>
              </div>
              
              <div className="input-group mb-3">
                <span className="input-group-text" id="password-addon">
                  <i className="fas fa-lock"></i>
                </span>
                <input type="password" name="password" id="password" autoComplete="off" 
                       value={password}
                       onChange={(e)=>setPassword(e.target.value)}
                       placeholder="Enter your password" className='form-control'/>
              </div>
              
              <div className="form-group form-button">
                <button type="submit" name="signin" id="signin" className="btn btn-primary btn-block"
                        onClick={getAccess}>
                  Log In
                </button>
              </div>
            </div>
          </form>
        </div>
      </>
    )
} 

export default AdminLogin;
