import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import convo from '../images/convo.gif';
import './Home.css';

const Home = () => {
    const [userName, setUserName] = useState();
    const [show, setShow] = useState(false);

    const userHome = async () => {
        try {
            const res = await fetch("/getdata", {
                method: "GET",
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json"
                },
                credentials: "include"
            });
            const data = await res.json();
            setUserName(data.name);
            setShow(true);
            if (!res.status === 200) {
                const error = new Error(res.err);
                throw error;
            }
        } catch (err) {
            console.log(err);
        }
    }

    useEffect(() => {
        userHome();
    }, [])

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-5">
                    <img src={convo} alt="not found" />
                </div>
                <div className="col-md-7">
                    {userName ? (
                        <div>
                            <p className='maintext'>Hello {userName}! Welcome Back!! </p>
                        </div>
                    ) : (
                        <div>
                            <p className='maintext'>Please login to file a grievance</p>
                            <div className="d-flex justify-content-between mt-3">
                                <NavLink to="/login" className="btn btn-primary">Login</NavLink>
                                <NavLink to="/signup" className="btn btn-success">Sign Up</NavLink>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Home;
