import React, { useEffect, useState } from 'react';
import convo from '../images/convo.gif';
import backgroundImage from  '../images/bg.gif';
import './Home.css';

const Home = () => {
    const [userName, setUserName] = useState();
    const [show, setShow] = useState(false);

    const userHome = async () => {
        try {
            const res = await fetch("/getdata", {
                method: "GET",
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json"
                },
                credentials: "include"
            });
            const data = await res.json();
            setUserName(data.name);
            setShow(true);
            if (!res.status === 200) {
                const error = new Error(res.err);
                throw error;
            }
        } catch (err) {
            console.log(err);
        }
    }

    useEffect(() => {
        userHome();
    }, [])

    return (
        <div className="home-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <div className="row">
                <div className="col-md-55">
                    <img src={convo} alt="not found" style={{ width: '20%', height: '90%' }} /> {/* Adjust image width and height */}
                </div>
                <p className='maintext'>Hello {userName ? userName : ''}! Welcome Back!! </p>
            </div>
        </div>
    );
};

export default Home;
