import React from 'react';
import { NavLink } from 'react-router-dom';
import './Home.css';
import video from '.C:/Grievance-Portal-main/Grievance-Portal-main\client\src\images\video.mp4';

const Home = () => {
  return (
    <div className="container">
      {/* Video background */}
      <video autoPlay muted loop className="video-background">
        <source src="C:\Users\Dragon\Desktop\video.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      
      <div className="row mt-5">
        <div className="col-md-6 offset-md-3 text-center">
          <h1>Welcome to Grievance Management System</h1>
          <p className="mt-3">File your complaints easily with our system.</p>
          <div className="mt-4">
            <NavLink className="btn btn-primary mr-3" to="/login">Login</NavLink>
            <NavLink className="btn btn-success" to="/signup">Sign Up</NavLink>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
