import React, { useEffect, useState } from 'react';
import convo from '../images/convo.gif';

const Home = () => {
    const [userName, setUserName] = useState('');

    useEffect(() => {
        fetchUserData();
    }, []);

    const fetchUserData = async () => {
        try {
            const response = await fetch("/getdata", {
                method: "GET",
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json"
                },
                credentials: "include"
            });
            if (response.ok) {
                const userData = await response.json();
                setUserName(userData.name);
            } else {
                throw new Error('Failed to fetch user data');
            }
        } catch (error) {
            console.error('Error fetching user data:', error);
        }
    }

    return (
        <div className="container">
            <div className="row justify-content-center align-items-center">
                <div className="col-md-6 text-center">
                    <img src={convo} alt="Conversation" className="img-fluid mb-4" />
                    {userName ? (
                        <>
                            <h2 className="mb-3">Welcome Back, {userName}!</h2>
                            <p className="lead">File your grievances and track their status with ease.</p>
                        </>
                    ) : (
                        <>
                            <h2 className="mb-3">Student Grievance Management System</h2>
                            <p className="lead">Log in to manage your grievances or register for an account.</p>
                        </>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Home;
